mapper.insert:
INSERT INTO browsing_history (
    user_id, user_type, target_type, target_id, action_type,
    duration_seconds, additional_data, create_time
) VALUES (
    #{userId}, #{userType}, #{targetType}, #{targetId}, #{actionType},
    #{durationSeconds}, #{additionalData}, #{createTime}
)

mapper.select:
SELECT * FROM browsing_history
WHERE user_id = #{userId}
ORDER BY create_time DESC
LIMIT #{limit}

SELECT * FROM browsing_history
WHERE user_id = #{userId}
AND target_type = 'mentor'
AND action_type = 'view'
ORDER BY create_time DESC
LIMIT #{limit}
