mapper.update:
UPDATE mentors
name = #{name}, title = #{title}, institution = #{institution}, department = #{department}, email = #{email}, phone = #{phone}, office_location = #{officeLocation}, research_areas = #{researchAreas}, keywords = #{keywords}, bio = #{bio}, group_direction = #{groupDirection}, expected_student_qualities = #{expectedStudentQualities}, mentoring_style = #{mentoringStyle}, available_positions = #{availablePositions}, funding_status = #{fundingStatus}, collaboration_opportunities = #{collaborationOpportunities}, education_background = #{educationBackground}, avatar = #{avatar}, homepage_url = #{homepageUrl}, google_scholar_url = #{googleScholarUrl}, accepting_students = #{acceptingStudents}, max_students = #{maxStudents}, status = #{status}, is_verified = #{isVerified}, update_time = NOW()
WHERE id = #{id}

mapper.select:
SELECT * FROM mentors
WHERE status = 1 AND institution = #{institution}
ORDER BY rating_avg DESC, view_count DESC
LIMIT #{offset}, #{limit}

mapper.update:
UPDATE mentors
SET rating_avg = #{ratingAvg}, rating_count = #{ratingCount}, update_time = NOW()
WHERE id = #{id}
